<?php

namespace AppBundle\Repository;
use AppBundle\Entity\Box;

/**
 * BoxRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BoxRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAllWithType()
    {
        $query = $this->createQueryBuilder('b')
            ->leftJoin('b.type', 't')
                ->addSelect('t')
            ->leftJoin('b.project', 'p')
                ->addSelect('p')
            ->orderBy('b.project', 'ASC')
            ->addOrderBy('b.boxLetter', 'ASC')
            ->getQuery();

        return $query->getResult();
    }

    public function findByBoxWithTubesAndStrains($id)
    {
        $query = $this->createQueryBuilder('b')
            ->leftJoin('b.tubes', 't')
                ->addSelect('t')
            ->leftJoin('t.gmoStrain', 'g')
                ->addSelect('g')
            ->leftJoin('t.wildStrain', 'w')
                ->addSelect('w')
            ->where('b.id = :id' )
            ->setParameter('id', $id)
            ->getQuery();

        return $query->getOneOrNullResult();
    }
}
