<?php

namespace AppBundle\Repository;

use AppBundle\Entity\User;

/**
 * WildRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class WildStrainRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAllName(User $user)
    {
        $query = $this->createQueryBuilder('wild')
            ->innerJoin('wild.tubes', 'tubes')
            ->innerJoin('tubes.project', 'project')
            ->innerJoin('project.members', 'members')
            ->where('members = :user')
            ->setParameter('user', $user)
            ->select('wild.name')
            ->orderBy('wild.name', 'ASC')
            ->distinct()
            ->getQuery();

        return $query->getArrayResult();
    }

    public function findAllForUser(User $user, $limit = 10)
    {
        $query = $this->createQueryBuilder('strain')
            ->innerJoin('strain.tubes', 'tubes')
            ->innerJoin('tubes.box', 'boxes')
            ->innerJoin('boxes.project', 'projects')
            ->innerJoin('projects.members', 'members')
            ->innerJoin('strain.author', 'author')
                ->addSelect('author')
            ->innerJoin('projects.administrators', 'administrators')
            ->where('members = :user')
            ->orWhere('author = :user')
            ->orWhere('administrators = :user')
                ->setParameter('user', $user)
            ->distinct(true)
            ->orderBy('strain.id', 'DESC')
            ->setMaxResults($limit)
            ->getQuery();

        return $query->getResult();
    }

    public function findOneWithAll($strain)
    {
        $query = $this->createQueryBuilder('wild')
            ->innerJoin('wild.species', 'species')
                ->addSelect('species')
            ->innerJoin('species.genus', 'genus')
                ->addSelect('genus')
            ->innerJoin('wild.biologicalOriginCategory', 'category')
                ->addSelect('category')
            ->innerJoin('wild.tubes', 'tubes')
                ->addSelect('tubes')
            ->innerJoin('tubes.box', 'boxes')
                ->addSelect('boxes')
            ->innerJoin('boxes.project', 'projects')
                ->addSelect('projects')
            ->where('wild = :strain')
                ->setParameter('strain', $strain)
            ->getQuery();

        return $query->getOneOrNullResult();
    }
}
